const p={3:"<p>\u968F\u7740\u73B0\u4EE3\u793E\u4F1A\u7684\u4E0D\u65AD\u53D1\u5C55\uFF0C\u6309\u7167\u7528\u6237\u9700\u6C42\u6765\u5B9A\u5236\u751F\u4EA7\u5DF2\u7ECF\u6210\u4E3A\u4E00\u9879\u4EE4\u4EBA\u5411\u5F80\u7684\u4E8B\u60C5\u3002\u5728\u73B0\u4EE3\u793E\u4F1A\uFF0C\u51C6\u786E\u5730\u5B9A\u5236\u4F60\u6240\u9700\u7684\u4EA7\u54C1\uFF0C\u5E76\u968F\u65F6\u53EF\u4E0B\u5355\u8D2D\u4E70\uFF0C\u5DF2\u7ECF\u6210\u4E3A\u4E00\u79CD\u6807\u5FD7\u3002\u65E0\u8BBA\u662F\u62AB\u8428\u3001\u4E91\u89E3\u51B3\u65B9\u6848\u8FD8\u662F\u7167\u660E\u7CFB\u7EDF\uFF0C\u4F01\u4E1A\u6B63\u5728\u63D0\u4F9B\u66F4\u5E7F\u6CDB\u3001\u66F4\u7CBE\u7EC6\u7684\u9009\u62E9\uFF0C\u4EE5\u6EE1\u8DB3\u6D88\u8D39\u8005\u5BF9\u72EC\u7279\u4EA7\u54C1\u6216\u670D\u52A1\u7684\u671F\u671B\u3002</p><p><br></p><p>\u7136\u800C\uFF0C\u968F\u7740\u63D0\u4F9B\u66F4\u591A\u9009\u62E9\uFF0C\u53EF\u80FD\u7684\u7EC4\u5408\u5448\u6307\u6570\u589E\u957F\uFF0C\u51E0\u4E4E\u53EF\u4EE5\u8BF4\u662F\u65E0\u9650\u7684\u3002\u5982\u4F55\u5E94\u5BF9\u63A5\u8FD1\u65E0\u9650\u7684\u4EA7\u54C1\u7EC4\u5408\u7684\u4EA4\u4ED8\uFF0C\u8FD9\u662F\u7167\u660E\u7CFB\u7EDF\u5236\u9020\u5546Lumileds\u7ECF\u5E38\u9762\u4E34\u7684\u6311\u6218\u4E4B\u4E00\u3002</p><p><br></p><p>Lumileds\u65B0\u52A0\u5761\u5353\u8D8A\u4F9B\u5E94\u56E2\u961F\u7684\u9AD8\u7EA7\u7ECF\u7406\u89E3\u91CA\u9053\uFF1A\u201C\u6211\u4EEC\u7684\u4F9B\u5E94\u94FE\u590D\u6742\u6027\u6E90\u4E8E\u6211\u4EEC\u9AD8\u5EA6\u53EF\u914D\u7F6E\u7684\u4EA7\u54C1\u3002\u6211\u4EEC\u63A5\u6536\u5230\u7684\u5BA2\u6237\u8BA2\u5355\u53EF\u80FD\u6DB5\u76D6\u4E864,000\u591A\u79CD\u4EA7\u54C1\u7279\u6027\uFF08\u4F8B\u5982\u5149\u901A\u91CF\u6216\u989C\u8272\u7EC4\u5408\uFF09\uFF0C\u8FD9\u4E9B\u7279\u6027\u53EF\u80FD\u4E0E\u6211\u4EEC5,000\u591A\u4E2A\u88C5\u914D\u96F6\u4EF6\u53F7\u4E2D\u7684\u4EFB\u610F\u6570\u91CF\u76F8\u5BF9\u5E94\u3002\u6211\u4EEC\u5FC5\u987B\u5728\u5168\u7403\u591A\u4E2A\u5DE5\u5382\u548C\u4ED3\u5E93\uFF08\u5305\u62EC\u69DF\u57CE\u3001\u4E2D\u56FD\u3001\u65B0\u52A0\u5761\u548C\u5723\u4F55\u585E\uFF09\u768440,000\u4E2A\u4E0D\u540C\u539F\u6750\u6599\u5E93\u4E2D\u5BFB\u627E\u8FD9\u4E9B\u96F6\u4EF6\u3002\u201D</p><p>\u66F4\u590D\u6742\u7684\u662F\uFF0C\u8FD9\u4E9B\u8BA2\u5355\u7684\u6750\u6599\u9700\u8981\u8FC5\u901F\u627E\u5230\u548C\u5904\u7406\uFF0C\u56E0\u4E3ALumileds\u627F\u8BFA\u5728\u8BA2\u8D2D\u540E\u768448\u5C0F\u65F6\u5185\u786E\u8BA4\u548C\u5B89\u6392\u4EA7\u54C1\u4EA4\u4ED8\u3002</p><p> </p><p>\u6700\u8FD1\uFF0CLumileds\u5E0C\u671B\u5728\u5176\u6574\u4F53\u89C4\u5212\u5DE5\u4F5C\u6218\u7565\u6027\u80FD\u66F4\u589E\u5F3A\u4E00\u4E9B\u3002\u4F9B\u5E94\u94FE\u7BA1\u7406\u56E2\u961F\u8868\u793A\uFF1A\u201C\u6211\u4EEC\u7684\u6BCF\u4E2A\u5DE5\u5382\u90FD\u6709\u81EA\u5DF1\u72EC\u7279\u7684\u751F\u4EA7\u8FD0\u8425\u65B9\u5F0F\uFF0C\u6211\u4EEC\u5728\u4E86\u89E3\u5168\u7403\u751F\u4EA7\u60C5\u51B5\u65B9\u9762\u9762\u4E34\u7740\u6311\u6218\u3002\u5982\u679C\u6211\u4EEC\u7740\u773C\u8FBE\u6210\u4E1A\u52A1\u589E\u957F\uFF0C\u76EE\u524D\u5F88\u96BE\u5224\u65AD\u6211\u4EEC\u662F\u5426\u9700\u8981\u66F4\u591A\u7684\u4EA7\u80FD\u6216\u66F4\u591A\u7684\u4EBA\u529B\u3002\u6211\u4EEC\u4ECA\u5929\u53EF\u4EE5\u9AD8\u6548\u8FD0\u4F5C\uFF0C\u4F46\u5F88\u96BE\u786E\u5B9A\u4E0B\u5468\u3001\u4E0B\u4E2A\u6708\u6216\u660E\u5E74\u6211\u4EEC\u5E94\u8BE5\u63D0\u524D\u8BA1\u5212\u4EC0\u4E48\u3002\u201D</p><p> </p><p>\u4E3A\u4E86\u5B9E\u73B0\u8FD9\u4E00\u6218\u7565\u8BA1\u5212\uFF0CLumileds\u91C7\u7528\u4E86\u4F18\u5316\u6280\u672F\u6765\u652F\u6301\u5176\u5168\u7403\u9700\u6C42\u4F9B\u5E94\u94FE\u5339\u914D\u5DE5\u4F5C\uFF0C\u4EE5\u786E\u4FDD\u4EA4\u4ED8\u627F\u8BFA\u80FD\u591F\u5F97\u4EE5\u5C65\u884C\u3002\u5728\u4F18\u5316\u51B3\u7B56\u7CFB\u7EDF\u652F\u6301\u4E0B\uFF0C\u8BA1\u5212\u56E2\u961F\u53EF\u4EE5\u8FC5\u901F\u505A\u51FA\u8BA2\u5355\u5E94\u8BE5\u5728\u54EA\u4E2A\u5DE5\u5382\u3001\u4F55\u65F6\u751F\u4EA7\u7684\u51B3\u7B56\uFF0C\u91CD\u70B9\u5173\u6CE8\u5E93\u5B58\u4F18\u5316\u548C\u6309\u65F6\u5236\u9020\u4E4B\u95F4\u7684\u5E73\u8861\u3002</p><p> </p><p>Lumileds \u4F9B\u5E94\u94FE\u56E2\u961F\u8868\u793A\uFF1A\u201C\u6211\u4EEC\u73B0\u5728\u4E0D\u4EC5\u80FD\u591F\u770B\u5230\u6BCF\u4E2A\u5DE5\u5382\u7684\u4EA7\u91CF\uFF0C\u800C\u4E14\u6700\u91CD\u8981\u7684\u662F\u6211\u4EEC\u80FD\u591F\u8FDB\u884C\u60C5\u666F\u5206\u6790\u3002\u5982\u679C\u6211\u4EEC\u6539\u53D8\u4E86\u4E00\u4E9B\u4EA7\u80FD\u6216\u8D44\u6E90\u5206\u914D\uFF0C\u6211\u4EEC\u7684\u8BA1\u5212\u4EBA\u5458\u53EF\u4EE5\u9884\u6D4B\u76F8\u5E94\u7684\u8BA2\u5355\u751F\u4EA7\u548C\u4EA4\u4ED8\u65F6\u95F4\u7684\u53D8\u5316\u3002\u201D</p><p> </p><p>\u4F8B\u5982\uFF0C\u5728\u5206\u6790\u91CD\u8981\u8BA2\u5355\u65F6\uFF0C\u7CFB\u7EDF\u6700\u521D\u53EF\u80FD\u4F1A\u663E\u793ALumileds\u9700\u8981\u4E09\u5468\u4EE5\u4E0A\u7684\u65F6\u95F4\u6765\u751F\u4EA7\u6240\u9700\u7684LED\u7167\u660E\u9635\u5217\u3002\u7136\u540E\uFF0C\u8BA1\u5212\u4EBA\u5458\u53EF\u4EE5\u5F00\u59CB\u5206\u6790\u6F5C\u5728\u7684\u60C5\u51B5\uFF0C\u770B\u770B\u662F\u5426\u53EF\u4EE5\u901A\u8FC7\u8DE8\u57FA\u5730\u751F\u4EA7\u8F6C\u79FB\u6216\u91CD\u65B0\u5B89\u6392\u5DE5\u4F5C\u6D41\u7A0B\u6765\u7F29\u77ED\u4EA4\u4ED8\u65F6\u95F4\u3002</p><p> </p><p>\u4ED6\u4EEC\u8FDB\u4E00\u6B65\u89E3\u91CA\u9053\uFF1A\u201C\u6211\u4EEC\u53EF\u4EE5\u67E5\u770B\u5E93\u5B58\u72B6\u51B5\uFF0C\u5FEB\u901F\u786E\u5B9A\u95EE\u9898\u6240\u5728\uFF0C\u662F\u7531\u4E8E\u4EA7\u80FD\u9650\u5236\u8FD8\u662F\u5E93\u5B58\u6570\u636E\u4E0D\u8DB3\uFF1F\u5F53\u6211\u4EEC\u5FEB\u901F\u5206\u6790\u4F9B\u5E94\u94FE\u74F6\u9888\u540E\uFF0C\u4F18\u5316\u6280\u672F\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5FEB\u901F\u5236\u5B9A\u5E94\u5BF9\u8BA1\u5212\u3002\u201D</p><p> </p><p>\u6B64\u5916\uFF0CLumileds\u8FD8\u4F7F\u7528\u4F18\u5316\u7CFB\u7EDF\u6765\u652F\u6301\u53EF\u4EA4\u4ED8\u627F\u8BFA\u3002\u4ED6\u4EEC\u8865\u5145\u9053\uFF1A\u201C\u4E00\u65E6\u6211\u4EEC\u5BF9\u5E93\u5B58\u8FDB\u884C\u4E86\u826F\u597D\u7684\u5206\u7C7B\uFF0C\u4F18\u5316\u7CFB\u7EDF\u5C06\u5408\u7406\u5730\u5B89\u6392\u8BA2\u5355\u9700\u6C42\uFF0C\u4F18\u5316\u6280\u672F\u4F7F\u6211\u4EEC\u80FD\u591F\u5FEB\u901F\u751F\u6210\u8BA2\u5355\u786E\u8BA4\u4FE1\u606F\uFF0C\u4EE5\u6EE1\u8DB3\u5BA2\u6237\u7684\u8981\u6C42\u5E76\u5151\u73B0\u6211\u4EEC\u768448\u5C0F\u65F6\u4EA4\u4ED8\u627F\u8BFA\u3002\u201D</p><p> </p><p>\u501F\u52A9\u6570\u5B66\u4F18\u5316\u6280\u672F\uFF0CLumileds\u80FD\u591F\u66F4\u597D\u5730\u63D0\u9AD8\u6574\u4E2A\u751F\u4EA7\u5468\u671F\u7684\u957F\u671F\u6548\u7387\uFF0C\u5E76\u7B80\u5316\u62A5\u544A\u548C\u89C4\u5212\u5DE5\u4F5C\u3002\u4ED6\u4EEC\u603B\u7ED3\u8BF4\uFF1A\u201C\u6211\u4EEC\u5DF2\u7ECF\u770B\u5230\u4E86\u5728\u51B3\u7B56\u4F18\u5316\u65B9\u9762\u7684\u663E\u8457\u6539\u8FDB\uFF0C\u4E0E\u4EE5\u524D\u76F8\u6BD4\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u5C06\u6BCF\u5468\u6216SNOP\u8BA1\u5212\u7684\u5236\u5B9A\u65F6\u95F4\u51CF\u5C11\u4E86\u81F3\u5C1150%\u3002\u201D\u201D</p><p>\u901A\u8FC7\u5BF9\u5168\u7403\u5DE5\u5382\u548C\u4ED3\u5E93\u8FDB\u884C\u57FA\u4E8E\u6570\u636E\u7684\u4F9B\u5E94\u548C\u4EA7\u80FD\u8BC4\u4F30\u4E0E\u4F18\u5316\u8BA1\u7B97\uFF0CLumileds\u63D0\u9AD8\u4E86\u4EA4\u4ED8\u9884\u6D4B\u7684\u4E00\u81F4\u6027\u548C\u53EF\u9760\u6027\u3002</p><p><br></p><p>\u201C\u6211\u4EEC\u7684\u5BA2\u6237\u671F\u671B\u5F97\u5230\u660E\u786E\u7684\u627F\u8BFA\uFF0C\u6211\u4EEC\u6DF1\u77E5\u4F9B\u5E94\u94FE\u7684\u5B9E\u65F6\u60C5\u51B5\u3002\u6BCF\u5F53\u6211\u4EEC\u505A\u51FA\u4EA4\u4ED8\u627F\u8BFA\u65F6\uFF0C\u6211\u4EEC\u6E05\u695A\u4E86\u89E3\u6211\u4EEC\u53EF\u5229\u7528\u7684\u8D44\u6E90\uFF0C\u56E0\u6B64\u6211\u4EEC\u80FD\u591F\u7CBE\u786E\u5730\u6EE1\u8DB3\u5BA2\u6237\u6240\u671F\u671B\u7684\u4EA4\u4ED8\u65F6\u95F4\u8868\u3002\u201D \u901A\u8FC7\u6301\u7EED\u5730\u9075\u5B88\u8BA1\u5212\u548C\u4EA4\u4ED8\u671F\u9650\uFF0CLumileds\u79EF\u6781\u63D0\u5347\u5BA2\u6237\u7684\u4FE1\u5FC3\u6C34\u5E73\u548C\u6EE1\u610F\u5EA6\u3002</p>",4:"<p><strong>\u5229\u7528\u6570\u5B66\u516C\u5F0F\u505A\u51FA\u66F4\u597D\u7684\u51B3\u7B56</strong></p><p>\u5185\u5BB9\u6765\u81EA\uFF1A<a href='https://www.riotinto.com/en/news/stories/a-formula-better-decisions' target='blank'><u>https://www.riotinto.com/en/news/stories/a-formula-better-decisions</u></a></p><p> </p><p>\u6211\u4EEC\u5982\u4F55\u5229\u7528\u6570\u5B66\u6765\u8FD0\u8F93\u66F4\u591A\u94DD\u571F\u77FF\u5E76\u6BCF\u5E74\u8282\u7701\u6570\u767E\u4E07\u7F8E\u5143-\u529B\u62D3\u96C6\u56E2</p><p> </p><p>\u8FD9\u662F\u4E00\u9879\u7279\u522B\u4EE4\u4EBA\u795E\u7ECF\u7D27\u5F20\u7684\u5DE5\u4F5C\u3002 \u800C\u4E14\u98CE\u9669\u4E5F\u5F88\u9AD8\u3002</p><p>\u6211\u4EEC\u4F4D\u4E8E\u6FB3\u5927\u5229\u4E9A\u6606\u58EB\u5170\u5DDE\u7684\u97E6\u5E15\u94DD\u571F\u77FF\u4E1A\u52A1\u6BCF\u5E74\u8FD0\u8F93\u8D85\u8FC7 3000 \u4E07\u5428\u94DD\u571F\u77FF\u3002</p><p>\u6BCF\u5468\u6709\u591A\u8FBE\u5341\u8258\u5927\u578B\u8239\u8236\u9A76\u5165\u97E6\u5E15\u6E2F\uFF0C\u4E3A\u6FB3\u5927\u5229\u4E9A\u4E1C\u90E8\u3001\u4E9A\u6D32\u53CA\u5176\u4ED6\u5730\u533A\u7684\u5BA2\u6237\u8FD0\u9001\u94DD\u571F\u77FF\u3002 \u6CA1\u6709\u6CCA\u4F4D\u7684\u8239\u8236\u6BCF\u5929\u53EF\u80FD\u4F1A\u88AB\u5904\u4EE5\u9AD8\u8FBE 10 \u4E07\u6FB3\u5143\u7684\u7F5A\u6B3E\uFF0C\u6270\u4E71\u4E3A\u6E2F\u53E3\u63D0\u4F9B\u539F\u6599\u7684\u77FF\u5C71\u7684\u94DD\u571F\u77FF\u751F\u4EA7\uFF0C\u5E76\u5EF6\u8FDF\u5411\u5BA2\u6237\u4EA4\u8D27\u3002 \u6309\u7167\u9884\u7B97\u6309\u65F6\u8FDB\u6E2F\u3001\u88C5\u8D27\u548C\u51FA\u6E2F\u610F\u5473\u7740\u8981\u517C\u987E\u6F6E\u6C50\u3001\u65F6\u95F4\u3001\u5BA2\u6237\u3001\u5929\u6C14\u4EE5\u53CA\u6BCF\u8258\u8239\u6BCF\u5E74\u4EF7\u503C\u6570\u767E\u4E07\u7F8E\u5143\u7684 30 \u4E2A\u5355\u72EC\u51B3\u7B56\u3002</p><p> </p><p>\u8FD9\u662F\u6E2F\u53E3\u8BA1\u5212\u8C03\u5EA6\u5458 Michelle Sjoberg \u7684\u5DE5\u4F5C\u3002</p><p> </p><p>\u201C\u6211\u4EEC\u6BCF\u5929\u65E9\u4E0A\u90FD\u4F1A\u82B1\u4E09\u5230\u56DB\u4E2A\u5C0F\u65F6\u624B\u52A8\u8FD0\u884C\u573A\u666F\uFF0C\u4EE5\u54CD\u5E94\u5929\u6C14\u3001\u5230\u8FBE\u65F6\u95F4\u548C\u5E93\u5B58\u53EF\u7528\u6027\u4EE5\u53CA\u6211\u4EEC\u7684\u7ECF\u9A8C\u7B49\u53D8\u91CF\u7684\u6700\u65B0\u53D8\u5316\u3002\u201D</p><p> </p><p>\u8FD9\u5F88\u597D\uFF0C\u4F46\u5FC5\u987B\u6709\u66F4\u597D\u7684\u65B9\u6CD5\u3002</p><p> </p><p>\u786E\u5B9E\u5982\u6B64\u3002 \u529B\u62D3\u8058\u8BF7\u5DE5\u4E1A\u6570\u5B66\u5BB6\u5F00\u53D1\u8F6F\u4EF6\uFF0C\u5E2E\u52A9\u7C73\u6B47\u5C14\u7B49\u6E2F\u53E3\u8C03\u5EA6\u5458\u66F4\u4E00\u81F4\u3001\u66F4\u9891\u7E41\u5730\u505A\u51FA\u66F4\u597D\u7684\u51B3\u7B56\u3002 \u7ED3\u679C\u600E\u4E48\u6837\uFF1F \u66F4\u9AD8\u6548\u3001\u66F4\u4F4E\u6210\u672C\u7684\u8FD0\u8F93\u8FD0\u8425\u3002</p><p> </p><p>\u201C\u6211\u4EEC\u8FD0\u884C\u76F8\u540C\u7684\u573A\u666F\uFF0C\u5E76\u5728\u5927\u7EA6 30 \u5206\u949F\u5185\u505A\u51FA\u57FA\u4E8E\u4E8B\u5B9E\u56E0\u7D20\u7684\u51B3\u7B56\u3002\u6B64\u5916\uFF0C\u6211\u4EEC\u77E5\u9053\u6211\u4EEC\u6B63\u5728\u505A\u7684\u4E8B\u60C5\u662F\u6709\u6570\u636E\u652F\u6301\u7684\uFF0C\u800C\u4E0D\u662F\u610F\u89C1\uFF0C\u201D\u529B\u62D3\u751F\u4EA7\u529B\u4E0E\u6280\u672F\u90E8\u89C4\u5212\u548C\u8C03\u5EA6\u9996\u5E2D\u987E\u95EE\u7F57\u5FB7\xB7\u53F2\u5BC6\u65AF (Rod Smith) \u8BF4\u9053 </p><p> </p><p>\u800C\u4E14\u597D\u5904\u4E0D\u4EC5\u9650\u4E8E\u6E2F\u53E3\u3002</p><p> </p><p>\u6211\u4EEC\u53D1\u73B0\u4E86\u8BB8\u591A\u5176\u4ED6\u673A\u4F1A\uFF0C\u201C\u6570\u5B66\u4F18\u5316\u201D\uFF08\u8FD9\u662F\u884C\u4E1A\u672F\u8BED\uFF09\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5FEB\u901F\u51C6\u786E\u5730\u8BC4\u4F30\u66FF\u4EE3\u65B9\u6848\u3002 \u4ECE\u6211\u4EEC\u7B2C\u4E00\u6B21\u63A5\u89E6\u5730\u4E0B\u77FF\u77F3\u5230\u5411\u5BA2\u6237\u63D0\u4F9B\u6210\u54C1\uFF0C\u6211\u4EEC\u7684\u56E2\u961F\u53EF\u4EE5\u5728\u73B0\u5B9E\u4E16\u754C\u4E2D\u53D1\u751F\u7684\u95EE\u9898\u548C\u673A\u9047\u65F6\u505A\u51FA\u53CD\u5E94\u3002</p><p> </p><p> </p><p>\u201C\u5982\u679C\u6CA1\u6709\u57FA\u4E8E\u667A\u80FD\u6570\u5B66\u7684\u5DE5\u5177\uFF0C\u6211\u4EEC\u8FC7\u53BB\u5E38\u5E38\u6839\u636E\u6A21\u5F0F\u3001\u672C\u80FD\u548C\u5F53\u5730\u653F\u7B56\u6765\u8BA1\u5212\u548C\u53CD\u5E94\u2014\u2014\u8FD9\u610F\u5473\u7740\u6211\u4EEC\u6709\u65F6\u5F97\u5230\u7684\u7B54\u6848\u5B8C\u5168\u6B63\u786E\uFF0C\u5927\u591A\u6570\u65F6\u5019\u51E0\u4E4E\u6B63\u786E\uFF0C\u6709\u65F6\u5219\u975E\u5E38\u9519\u8BEF\uFF0C \u201D\u7F57\u5FB7\u8BF4\u3002 \u6362\u53E5\u8BDD\u8BF4\uFF0C\u5BF9\u4E8E\u529B\u62D3\u6765\u8BF4\u8FD8\u4E0D\u591F\u597D\u3002</p><p> </p><p>\u201C\u6570\u5B66\u4F18\u5316\u4E0D\u4F1A\u4EE3\u66FF\u64CD\u4F5C\u5458\u7684\u51B3\u5B9A\u6743\uFF0C\u76F8\u53CD\uFF0C\u5B83\u53EF\u4EE5\u5E2E\u52A9\u4ED6\u4EEC\u66F4\u9891\u7E41\u5730\u505A\u51FA\u66F4\u597D\u7684\u51B3\u7B56\u3002\u201D</p><p>Rod Smith\uFF0C\u89C4\u5212\u548C\u8C03\u5EA6\u9996\u5E2D\u987E\u95EE</p><p> </p><p>\u5728\u6B64\u8FC7\u7A0B\u4E2D\uFF0C\u6570\u5B66\u4F18\u5316\u6280\u672F\u4E3A\u529B\u62D3\u8282\u7701\u4E86\u65F6\u95F4\u548C\u6570\u767E\u4E07\u7F8E\u5143\u3002</p><p> </p><p>\u8FD9\u662F\u6253\u5F00\u4E00\u4E2A\u6109\u5FEB\u7684\u65E9\u6668\u7684\u6700\u4F18\u65B9\u6CD5\u3002</p>",5:"<p>\u8FD1\u5E74\u6765\uFF0C\u6211\u56FD\u7684\u6C7D\u8F66\u4EA7\u4E1A\u4F5C\u4E3A\u652F\u67F1\u884C\u4E1A\uFF0C\u6C7D\u8F66\u4FDD\u6709\u91CF\u6301\u7EED\u8FC5\u901F\u589E\u957F\u3002\u540C\u65F6\u6C7D\u8F66\u884C\u4E1A\u6B63\u671D\u7740\u7535\u52A8\u5316\u3001\u667A\u80FD\u5316\u3001\u4E92\u8054\u5316\u548C\u5171\u4EAB\u5316\u65B9\u5411\u53D1\u5C55\u3002\u968F\u7740\uFF0C\u4EBA\u5DE5\u667A\u80FD\u3001\u5927\u6570\u636E\u7B49\u65B0\u5174\u6280\u672F\u4E0E\u4F20\u7EDF\u6C7D\u8F66\u6280\u672F\u7684\u878D\u5408\uFF0C\u4E5F\u8BA9\u6C7D\u8F66\u884C\u4E1A\u5448\u73B0\u51FA\u65B0\u683C\u5C40\u3002\u800C\u6C7D\u8F66\u7684\u5B9E\u9A8C\u6DB5\u76D6\u4E86\u6574\u4E2A\u4EA7\u54C1\u8BBE\u8BA1\u548C\u5F00\u53D1\u8FC7\u7A0B\uFF0C\u5176\u91CD\u8981\u6027\u4E0D\u8A00\u800C\u55BB\u3002</p><p> </p><p>\u5BF9\u4E8E\u4E00\u4E2A\u5168\u65B0\u6C7D\u8F66\u7814\u53D1\u9879\u76EE\u800C\u8A00\uFF0C\u9700\u8981\u8FDB\u884C5000\u591A\u9879\u5173\u952E\u96F6\u90E8\u4EF6\u5B9E\u9A8C\u3001200\u591A\u9879\u7CFB\u7EDF\u7EA7\u5B9E\u9A8C\uFF0C\u4EE5\u53CA600\u591A\u9879\u6574\u8F66\u7EA7\u5B9E\u9A8C\u3002\u6574\u4E2A\u5B9E\u9A8C\u8FC7\u7A0B\u901A\u5E38\u9700\u898136\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u6D89\u53CA\u591A\u4E2A\u5B9E\u9A8C\u533A\u57DF\uFF0C\u5305\u62EC\u5404\u79CD\u5B9E\u9A8C\u5BA4\u3001\u8BD5\u8F66\u573A\u3001\u8010\u5019\u6027\u5B9E\u9A8C\u57FA\u5730\u7B49\uFF0C\u9700\u8981\u6D4B\u8BD5\u6210\u5343\u4E0A\u4E07\u4E2A\u96F6\u90E8\u4EF6\u6837\u672C\u548C\u8FD1\u5343\u4E2A\u6574\u8F66\u53CA\u7CFB\u7EDF\u5B9E\u9A8C\u6837\u672C\u3002\u5B9E\u9A8C\u65F6\u95F4\u8DE8\u5EA6\u957F\u3001\u6DB5\u76D6\u533A\u57DF\u5E7F\u6CDB\u4EE5\u53CA\u6D89\u53CA\u591A\u4E2A\u73AF\u8282\u7684\u7279\u70B9\u4F7F\u5F97\u5B9E\u9A8C\u8D44\u6E90\u7684\u5408\u7406\u5206\u914D\u663E\u5F97\u5C24\u4E3A\u91CD\u8981\u3002</p><p> </p><p>\u5982\u4F55\u5E94\u7528\u6570\u5B57\u5316\u548C\u667A\u80FD\u5316\u6280\u672F\uFF0C\u5EFA\u7ACB\u4EE5\u4F18\u5316\u6392\u7A0B\u4E3A\u57FA\u7840\u7684\u81EA\u52A8\u804C\u80FD\u5B9E\u9A8C\u6392\u7A0B\u7CFB\u7EDF\uFF0C\u5BF9\u4E8E\u8FDB\u4E00\u6B65\u4F18\u5316\u6C7D\u8F66\u4EA7\u54C1\u5F00\u53D1\u8FC7\u7A0B\u5177\u6709\u91CD\u8981\u610F\u4E49\u3002</p><p> </p><p><span style='color: rgb(55, 65, 81); background-color: rgb(247, 247, 248);'>\u6CDB\u4E9A\u6C7D\u8F66\u6280\u672F\u4E2D\u5FC3\u62E5\u6709\u6C7D\u8F66\u884C\u4E1A\u9886\u5148\u7684\u5B9E\u9A8C\u5BA4\uFF0C\u8D1F\u8D23\u6574\u8F66\u4EA7\u54C1\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u5404\u7C7B\u6D4B\u8BD5\u9700\u6C42\uFF0C\u6D4B\u8BD5\u80FD\u529B\u8986\u76D6\u4E86\u6574\u8F66\u3001\u7CFB\u7EDF\u548C\u5173\u952E\u96F6\u90E8\u4EF6\u3002\u4E2D\u5FC3\u5DF2\u7ECF\u5EFA\u8BBE\u5E76\u62E5\u6709\u4E00\u7CFB\u5217\u4E16\u754C\u9886\u5148\u7684\u5B9E\u9A8C\u8BBE\u65BD\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\u6574\u8F66\u70ED\u529B\u5B66\u98CE\u6D1E\u3001\u8F66\u8F86\u5B89\u5168\u5B9E\u9A8C\u5BA4\u3001\u632F\u52A8\u566A\u58F0\u5B9E\u9A8C\u5BA4\u3001\u6750\u6599\u5B9E\u9A8C\u5BA4\u3001\u6392\u653E\u5B9E\u9A8C\u5BA4\u3001\u6574\u8F66\u6027\u80FD\u5B9E\u9A8C\u5BA4\u3001\u52A8\u529B\u603B\u6210\u5B9E\u9A8C\u5BA4\u3001\u5E95\u76D8\u8FD0\u52A8\u4E0E\u52A8\u529B\u5B66\u53C2\u6570\u6D4B\u91CF\u5B9E\u9A8C\u5BA4\u3001\u7ED3\u6784\u5B9E\u9A8C\u5BA4\u3001\u7535\u5B50\u7535\u5668\u5B9E\u9A8C\u5BA4\u4EE5\u53CA\u65B0\u80FD\u6E90\u5B9E\u9A8C\u5BA4\u7B49\u5341\u4E00\u5927\u7C7B\u5B9E\u9A8C\u5BA4\u3002\u6B64\u5916\uFF0C\u8BE5\u4E2D\u5FC3\u8FD8\u62E5\u6709\u56FD\u5185\u89C4\u6A21\u6700\u5927\u3001\u529F\u80FD\u6700\u9F50\u5168\u7684\u4E13\u4E1A\u6C7D\u8F66\u8BD5\u8F66\u573A\u2014\u2014\u5E7F\u5FB7\u8BD5\u8F66\u573A\u3002</span></p><p><span style='color: rgb(55, 65, 81); background-color: rgb(247, 247, 248);'> </span></p><p>\u6CDB\u4E9A\u652F\u6301\u7684\u7814\u53D1\u9886\u57DF\u62E5\u6709\u591A\u79CD\u8BD5\u9A8C\u89C4\u8303\uFF0C\u6BCF\u79CD\u8BD5\u9A8C\u7684\u6267\u884C\u8FC7\u7A0B\u5404\u6709\u5DEE\u5F02\uFF0C\u8FD9\u5BFC\u81F4\u8BD5\u9A8C\u6392\u7A0B\u5DE5\u4F5C\u590D\u6742\u7E41\u7410\uFF0C\u540C\u65F6\u4E5F\u4F7F\u8D44\u6E90\u5229\u7528\u60C5\u51B5\u96BE\u4EE5\u6E05\u6670\u4E86\u89E3\u3002\u6CDB\u4E9A\u76EE\u524D\u7684\u8BD5\u9A8C\u4E1A\u52A1\u4E3B\u8981\u4F9D\u8D56\u4E8E\u4E00\u6B3E\u8001\u65E7\u7684\u6392\u7A0B\u8F6F\u4EF6\uFF0C\u8BE5\u8F6F\u4EF6\u5B58\u5728\u8BF8\u591A\u95EE\u9898\uFF0C\u5305\u62EC\u6A21\u578B\u5EFA\u8BBE\u73AF\u5883\u5C01\u95ED\u3001\u4E1A\u52A1\u903B\u8F91\u4E0D\u6E05\u6670\u3001\u8BA1\u7B97\u8FC7\u7A0B\u5BF9\u7528\u6237\u4E0D\u53EF\u89C1\u3001\u7528\u6237\u4E92\u52A8\u6027\u6709\u9650\u7B49\u3002\u56E0\u6B64\uFF0C\u7528\u6237\u5E38\u5E38\u9700\u8981\u7B49\u5F85\u6570\u5341\u4E2A\u5C0F\u65F6\u751A\u81F3\u6570\u5929\u624D\u80FD\u83B7\u53D6\u8BD5\u9A8C\u7ED3\u679C\uFF0C\u65E0\u6CD5\u6EE1\u8DB3\u4E1A\u52A1\u9700\u6C42\u3002</p><p> </p><p>\u6CDB\u4E9A\u5B9E\u9A8C\u7BA1\u7406\u56E2\u961F\u5E0C\u671B\u901A\u8FC7\u91CD\u65B0\u8BBE\u8BA1\u8BD5\u9A8C\u81EA\u52A8\u6392\u7A0B\u7CFB\u7EDF\uFF0C\u4F18\u5316\u4E1A\u52A1\u6D41\u7A0B\uFF0C\u7EFC\u5408\u8003\u8651\u73B0\u6709\u7684\u4E1A\u52A1\u903B\u8F91\u548C\u6F5C\u5728\u7528\u6237\u9700\u6C42\u3002\u8FD9\u4E00\u91CD\u5851\u7684\u76EE\u6807\u662F\u5B9E\u73B0\u4E1A\u52A1\u6D41\u7A0B\u7684\u53EF\u63A7\u6027\u548C\u6570\u5B57\u5316\uFF0C\u771F\u6B63\u5B9E\u73B0\u8BD5\u9A8C\u4EFB\u52A1\u6392\u7A0B\u7684\u81EA\u52A8\u5316\uFF0C\u4ECE\u800C\u63D0\u9AD8\u5DE5\u4F5C\u6548\u7387\u548C\u8D44\u6E90\u5229\u7528\u7387\u3002</p><p> </p><p> </p><p>OAT\u4F18\u5316\u54A8\u8BE2\u56E2\u961F\u5F00\u53D1\u5E76\u5B9E\u65BD\u7684\u8FD0\u7B79\u4F18\u5316\u7CFB\u7EDF\u5305\u542B\u4E24\u4E2A\u4E3B\u8981\u529F\u80FD\u6A21\u5757\uFF1A\u6570\u5B66\u6A21\u578B\u7684\u6784\u5EFA\u4E0E\u6C42\u89E3\u8FC7\u7A0B\u3002\u5728\u6570\u5B66\u6A21\u578B\u7684\u8BBE\u8BA1\u65B9\u9762\uFF0C\u6211\u4EEC\u91C7\u7528\u4E86\u9AD8\u6548\u7684JAVA\u8BED\u8A00\u8FDB\u884C\u6784\u5EFA\uFF0C\u4EE5\u786E\u4FDD\u6A21\u578B\u5177\u5907\u901A\u7528\u6027\u3001\u53EF\u6269\u5C55\u6027\u3001\u7075\u6D3B\u6027\u548C\u9C81\u68D2\u6027\u3002\u5B9E\u65BD\u56E2\u961F\u4E0E\u6CDB\u4E9A\u6280\u672F\u56E2\u961F\u7D27\u5BC6\u5408\u4F5C\u4E868\u4E2A\u6708\uFF0C\u7CBE\u5FC3\u4FDD\u8BC1\u4E86\u6A21\u578B\u7684\u5F00\u653E\u6027\u548C\u7075\u6D3B\u6027\uFF0C\u663E\u8457\u63D0\u5347\u4E86\u6C42\u89E3\u6548\u7387\u3002</p><p> </p><p>\u540C\u65F6\uFF0C\u901A\u8FC7\u6DF1\u5165\u89E3\u6790\u5F53\u524D\u6A21\u578B\uFF0C\u6211\u4EEC\u8BE6\u7EC6\u68B3\u7406\u4E86\u73B0\u6709\u7684\u4E1A\u52A1\u89C4\u5219\uFF0C\u5E76\u5145\u5206\u6316\u6398\u4E86\u7528\u6237\u7684\u4F7F\u7528\u9700\u6C42\u3002\u8FD9\u6837\uFF0C\u6211\u4EEC\u80FD\u591F\u786E\u4FDD\u4F18\u5316\u6A21\u578B\u7684\u8FD0\u7B97\u7ED3\u679C\u80FD\u591F\u88AB\u4E1A\u52A1\u5355\u4F4D\u5145\u5206\u5229\u7528\uFF0C\u4F7F\u66F4\u591A\u7528\u6237\u80FD\u591F\u8F7B\u677E\u4F7F\u7528\u4F18\u5316\u6392\u7A0B\u7CFB\u7EDF\uFF0C\u4EAB\u53D7\u66F4\u4E3A\u4FBF\u6377\u5FEB\u901F\u7684\u8FD0\u8425\u73AF\u5883\uFF0C\u4EE5\u53CA\u4F18\u5316\u79D1\u6280\u6240\u5E26\u6765\u7684\u6548\u76CA\u3002</p>"};export{p as d};
